mapper.select:
select sm.task_id, sm.name, sm.vendor_id, cv.vendor_name, sm.create_user, (select cc.moble from c_customer cc where cc.customer_id = sm.create_user) as create_user_name, sm.agent, (select xm.manager_name from xt_manager xm where xm.manager_id = sm.agent) as agent_name, sm.task_type_id, stt.`name` as task_type_name, sm.create_time as publish_time, sm.execute_times, sm.salary, sm.plat_fee, sm.status, sm.remark, sm.audit_times, (select count(*) from s_media_execute sse where sse.task_id = sm.task_id) as total_recovery, (select count(*) from s_media_execute sse where sse.task_id = sm.task_id and sse.`status` = 4 ) as audit_pass, sm.do_btime, sm.do_etime
FROM s_media sm,c_vendor cv ,s_task_type stt WHERE sm.vendor_id = cv.vendor_id AND stt.father_id = #{father_id} AND sm.task_type_id = stt.task_type_id AND sm.is_del = 0 AND sm.status > 1 AND sm.status < 7 AND sm.status <> 3
and sm.task_id = #{task_id}
and sm.name LIKE CONCAT('%',#{name},'%')
and sm.task_type_id = #{task_type}
and cv.vendor_name LIKE CONCAT('%',#{vendor_name},'%')
and sm.status = #{status}
ORDER BY sm.task_id desc
limit #{start},#{rows}

	   	select count(1)
		FROM  s_media sm,c_vendor cv,s_task_type stt
		WHERE sm.vendor_id = cv.vendor_id
		AND sm.task_type_id = stt.task_type_id 
		AND stt.father_id = #{father_id}
		AND sm.is_del = 0 
	 	AND sm.status > 1 AND sm.status < 7 AND sm.status <> 3
   		
			and sm.task_id = #{task_id}
		
    	
			and sm.name LIKE CONCAT('%',#{name},'%') 
		
		
			and sm.task_type_id = #{task_type}
		
		
			and cv.vendor_name LIKE CONCAT('%',#{vendor_name},'%') 
		
		
			and sm.status = #{status}

select sme.execute_id, sm.task_id, sm.name, sm.vendor_id, cv.vendor_name, sm.create_user, (select cc.moble from c_customer cc where cc.customer_id = sm.create_user) as create_user_name, sm.agent, (select xm.manager_name from xt_manager xm where xm.manager_id = sm.agent) as agent_name, sm.task_type_id, stt.`name` as task_type_name, sm.create_time as publish_time, sm.execute_times, sm.salary, sm.plat_fee, sm.status, sme.status as execute_status, au.moble, sme.executor, sme.upload_time, sm.remark, sme.acceptor, (select cc.moble from c_customer cc where cc.customer_id = sme.acceptor) as acceptor_name, sme.accept_time, sme.agent as audit_agent, (select xm.manager_name from xt_manager xm where xm.manager_id = sme.agent) as audit_agent_name, sm.create_time as publish_time, sm.audit_times FROM s_media sm,s_media_execute sme,c_vendor cv ,app_user au,s_task_type stt WHERE sm.task_id = sme.task_id AND sm.vendor_id = cv.vendor_id AND sme.executor = au.user_id AND stt.father_id = #{father_id} AND sm.task_type_id = stt.task_type_id AND sm.is_del = 0 AND sm.status = 2 AND sme.status = 3 AND sm.create_type = 0
and sm.task_id = #{task_id}
and sm.name LIKE CONCAT('%',#{name},'%')
and sm.task_type_id = #{task_type}
and cv.vendor_name LIKE CONCAT('%',#{vendor_name},'%')
and sme.execute_id = #{execute_id}
and sme.executor = (select user_id from app_user au where au.moble = #{moble})
ORDER BY sme.execute_id
limit #{start},#{rows}

select count(1) FROM s_media sm,s_media_execute sme,c_vendor cv,s_task_type stt WHERE sm.task_id = sme.task_id AND sm.vendor_id = cv.vendor_id AND sm.task_type_id = stt.task_type_id AND stt.father_id = #{father_id} AND sm.is_del = 0 AND sm.status = 2 AND sme.status = 3 AND sm.create_type = 0
and sm.task_id = #{task_id}
and sm.name LIKE CONCAT('%',#{name},'%')
and sm.task_type_id = #{task_type}
and cv.vendor_name LIKE CONCAT('%',#{vendor_name},'%')
and sme.execute_id = #{execute_id}
and sme.executor = (select user_id from app_user au where au.moble = #{moble})

   	select count(1)
	from  s_media_execute sse,s_media ss
	where sse.task_id = ss.task_id
	and sse.task_id = #{task_id}
	and sse.status = #{status}
	and ss.is_del = 0

   	select count(1)
	from  s_media_pt smt
	where smt.task_id = #{task_id}

mapper.update:
UPDATE APP_USER  
SET balance = balance + #{salary} 
WHERE user_id = #{executor}
