mapper.select:
SELECT pc.category_id, pc.category_name, pc.create_user, pc.create_time, pc.is_del FROM product_category pc WHERE pc.is_del = 0
AND pc.category_name LIKE CONCAT('%',#{category_name_search},'%')
AND pc.category_id = #{category_id_search}

mapper.insert:
        INSERT INTO product_category (
			category_name,
			create_user
        )
        VALUES 
        (#{category_name,jdbcType=VARCHAR},
         #{create_user,jdbcType=VARCHAR}
         )
